from base64 import *def hexTob64(string):	return byteStringToB64(hexToByteString(string))def hexToByteString(string):	return convertToByteString(int(string, 16))def b64ToHex(string):	return byteStringToHex(b64ToByteString(string))def b64ToByteString(string):	return b64decode(string)def byteStringToHex(byteString):	output = 0	copyString = byteString	while len(copyString) > 0:		output += ord(copyString[0])		copyString = copyString[1:]		output = output << 8	toreturn = hex(output)[2:].rstrip("00L")	if len(toreturn)%2 != 0:		return '0'+toreturn	return toreturndef byteStringToB64(byteString):	return b64encode(byteString)def convertToByteString(integer, bigEndian = True):	output = ""	while (integer > 0):		output = chr(integer & 255) + output if bigEndian else output + chr(integer & 255)		integer = integer >> 8	return output